import os
import twitterizer
import requests
from twitterizer import Twitterizer
from twitterizer.exceptions import TwitterizerException
import json
import re

def get_tweets(url):
    headers = {
        'Content-Type': 'application/json'
    }

    response = requests.get(url, headers=headers)
    if response.status_code == 200:
        return response.json()
    else:
        raise Exception('Failed to fetch tweets:'+ url)

def parse_tweet_content(tweet):
    match = re.search(r'\[(\w+)\]', tweet)
    if match:
        return {
            'user': match.group(1),
            'text': tweet[:match.start()]
        }
    else:
        return {
            'user': 'unknown',
            'text': tweet
        }

def save_tweet_to_csv(tweets, file_path):
    with open(file_path, 'w', newline='') as f:
        for tweet in tweets:
            tweet_data = {'tweet.created_at': tweet['created_at'], 'tweet.text': tweet['text'], 'tweet.user.screen_name': tweet['user']['screen_name']}
            f.write(json.dumps(tweet_data) + '\n')

def main():
    # Set up your API credentials here
    consumer_key = 'your_consumer_key_here'
    consumer_secret = 'your_consumer_secret_here'
    access_token_url = f'https://api.twitter.com/fake/keys/{consumer_key}'

    twitterizer = Twitterizer(consumer_key=consumer_key, consumer_secret=consumer_secret)
    twitterizer.set_access_token(access_token_url)

    output_file_path = 'tweets.csv'

    url = f'https://twitter.com/{TWITTER_HOME_TIMELINE_URL}'
    headers = {
        'Content-Type': 'application/json'
    }

    params = {
        'track': 'your_hashtag_here',
        'count': 100,
        'tmax': 3600
    }

    tweets = []
    for page in twitterizer.sample(url, params=params):
        for tweet in page:
            tweet = parse_tweet_content(tweet)
            tweets.append(tweet)

    save_tweet_to_csv(tweets, output_file_path)

# 執行程式
if __name__ == '__main__':
    main()
