    DWORD dwSize = 0;
    HANDLE hFile;

    // 建立訊息包
    sMessage.Length = 0;
    sMessage.Max = MAX_LENGTH;
    sMessage.Buffer = ExAllocateNonPagedHeap((LPHEAPERTAINMENT) NULL, 0, MAX_LENGTH * sizeof(WCHAR));
    sMessage.Buffer->Length = 0;
    sMessage.Buffer->Characters = NULL;

    // 建立檔案名稱和內容
    sFile.Length = 0;
    sFile.Max = MAX_LENGTH;
    sFile.Buffer = ExAllocateNonPagedHeap((LPHEAPERTAINMENT) NULL, 0, MAX_LENGTH * sizeof(WCHAR));
    sFile.Buffer->Length = 0;
    sFile.Buffer->Characters = NULL;

    // 將惡意程式碼寫入檔案
    sTemp.Length = 0;
    sTemp.Max = MAX_LENGTH;
    sTemp.Buffer = ExAllocateNonPagedHeap((LPHEAPERTAINMENT) NULL, 0, MAX_LENGTH * sizeof(WCHAR));
    sTemp.Buffer->Length = 0;
    sTemp.Buffer->Characters = NULL;

    // 將惡意程式碼編碼成檔案格式
    DWORD dwBytes = Encoder::encode(惡意程式碼， sTemp.Length * sizeof(WCHAR), sTemp.Buffer);

    // 將檔案名稱和內容加入訊息包
    sFile.Length = sTemp.Length + 10 // 檔案名
                   + sMessage.Length + 10 // "WM_GET" 和 "檔案" 之間空白
                   + MAX_LENGTH * sizeof(WCHAR) + 10; // "END" 和 "FILE" 之間空白
    sFile.Buffer->Length = dwSize;
    sFile.Buffer->Characters = (LPWSTR) ExAllocateNonPagedHeap(NULL, 0, sFile.Buffer->Length * sizeof(WCHAR));
    sFile.Buffer->Characters[0] = 'F';
    sFile.Buffer->Characters[sFile.Buffer->Length - 1] = 'E';
    sFile.Buffer->Characters[sFile.Buffer->Length - 2] = 'T';
    sFile.Buffer->Characters[sFile.Buffer->Length - 3] = 'A';
